var request = new sn_ws.RESTMessageV2();
request.setEndpoint('https://dev45932.service-now.com/api/now/table/x_191536_gear_poc_gearcustomtable');
request.setHttpMethod('POST');

//Eg. UserName="admin", Password="admin" for this code sample.
var user = 'admin';
var password = 'Sokor2019';

request.setBasicAuth(user,password);
request.setRequestHeader("Accept","application/json");
//request.setRequestHeader('Content-Type','application/json');request.setRequestBody("{\"custom_text\":\"MyCustomText\",\"global_app_id\":\"MyGlobalAppId\"}");


request.setRequestHeader('Content-Type','application/json');request.setRequestBody(
	"{\"custom_text\":\""
	+ current.gear_appname + "\""
	+ ",\"global_app_id\":\""
	+ current.gear_globalappid
	+ "\"}");

var response = request.execute();
//gs.log(response.getBody());

// gs.addInfoMessage("Global App ID: " + current.gear_globalappid);
// gs.addInfoMessage("Global App ID: " + current.gear_appname);

var httpStatus = response.getStatusCode();
//	gs.addInfoMessage("Get status code: " + httpStatus);
	if (httpStatus == 201) {
		workflow.scratchpad.httpStatus_ = '201';
//		gs.addInfoMessage("New Status code: " + workflow.scratchpad.httpStatus_);
	}

